import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../store/authSlice';
import styles from './Layout.module.css';

const Layout = ({ children }) => {
  const history = useHistory();
  const dispatch = useDispatch();
  const isAuthenticated = useSelector(state => !!state.auth.token);

  const handleLogout = () => {
    dispatch(logout());
    history.push('/login');
  };

  return (
    <div className={styles.layout}>
      <header className={styles.header}>
        <nav className={styles.nav}>
          <Link to="/" className={styles.logo}>Africhama</Link>
          <ul className={styles.navItems}>
            <li><Link to="/">Home</Link></li>
            {isAuthenticated ? (
              <>
                <li><Link to="/profile">Profile</Link></li>
                <li><Link to="/network">My Network</Link></li>
                <li><Link to="/gift-system">Gift System</Link></li>
                <li><Link to="/subscription">Subscription</Link></li>
                <li><Link to="/support">Support</Link></li>
                <li><button onClick={handleLogout} className={styles.logoutBtn}>Logout</button></li>
              </>
            ) : (
              <>
                <li><Link to="/login">Login</Link></li>
                <li><Link to="/register">Register</Link></li>
              </>
            )}
          </ul>
        </nav>
      </header>
      <main className={styles.main}>
        {children}
      </main>
      <footer className={styles.footer}>
        <p>&copy; 2023 Africhama. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Layout;
