import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Import components
import Layout from './components/Layout';
import PrivateRoute from './components/PrivateRoute';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Profile from './pages/Profile';
import NetworkAndUpgrade from './pages/NetworkAndUpgrade';
import GiftSystem from './pages/GiftSystem';
import SubscriptionMaintenance from './pages/SubscriptionMaintenance';
import Support from './pages/Support';

// Import styles
import './styles/global.css';

function App() {
  const isAuthenticated = useSelector(state => state.auth.isAuthenticated);

  return (
    <Router>
      <Layout>
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/login">
            {isAuthenticated ? <Redirect to="/profile" /> : <Login />}
          </Route>
          <Route path="/register">
            {isAuthenticated ? <Redirect to="/profile" /> : <Register />}
          </Route>
          <PrivateRoute path="/profile" component={Profile} />
          <PrivateRoute path="/network" component={NetworkAndUpgrade} />
          <PrivateRoute path="/gift-system" component={GiftSystem} />
          <PrivateRoute path="/subscription" component={SubscriptionMaintenance} />
          <PrivateRoute path="/support" component={Support} />
          <Route path="*">
            <Redirect to="/" />
          </Route>
        </Switch>
      </Layout>
      <ToastContainer 
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </Router>
  );
}

export default App;
